editor.css:
.LOC-Creative .et-body {
  font-family: "Roboto", "Helvetica", Arial, sans-serif;
  font-feature-settings: 'liga';
  -webkit-font-smoothing: antialiased;
  text-rendering: optimizeLegibility;
  -moz-osx-font-smoothing: auto;
}

.LOC-Creative .et_card {
  padding: 15px;
}

.LOC-Creative .extra-large-header {
  font-size: 22px;
  line-height: 22px;
  font-weight: 500;
  letter-spacing: -0.4px;
  margin: 0 0 10px 0;
  color: #242424;
  margin-bottom: 20px
}

.LOC-Creative .body-text {
  font-weight: 400;
  font-size: 13px;
  letter-spacing: -0.21px;
  line-height: 1.4;
  color: #444444;
  margin-bottom: 30px;
}

.LOC-Creative .btn {
    padding: 12px 30px 13px;
    font-size: 18px;
    line-height: 18px;
    min-height: 45px;
    display: inline-block;
    text-align: center;
    white-space: initial;
    vertical-align: middle;
    -ms-touch-action: manipulation;
    touch-action: manipulation;
    cursor: pointer;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    background-image: none;
    border-style: solid;
    border-width: 1px;
    border-radius: 2px;
    margin: 0 0 15px 0;  // edited
    font-weight: 500;
}

.LOC-Creative .btn-primary {
    color: #FFFFFF;
    background-color: #5627D8;
    border-color: #5627D8;
}

.LOC-Creative .btn-block {
    display: block;
    width: 100%;
}

.LOC-Creative .btn-primary-outline {
    color: #5627D8;
    background-color: transparent;
    border-color: #5627D8;
}

.LOC-Creative .btn-primary:active {
  background-color: #4f19b9;
  border-color: #4f19b9;
  outline-offset: -2px;
}

.LOC-Creative .btn-primary-outline:active {
  color: #4f19b9;
  background-color: transparent;
  border-color: #4f19b9;
  outline-offset: -2px;
}

.btn-link {
  border: 0;
  border-radius: 0;
  color: #3F05E0;
  font-size: 13px;
  font-weight: 400;
  background-color: transparent;
  border-color: transparent;
  padding: 0;
}
.btn-link:active, .btn-link:hover {
    color: #2E49F8;
}

.LOC-Creative .vertical-offset-clear {
  margin-bottom: 0;
}
